"use strict";document.addEventListener("DOMContentLoaded",()=>{if(document.querySelector(".burger")){const t=document.querySelector(".burger"),r=document.querySelector(".header__menu"),c=document.querySelector("body");t.addEventListener("click",()=>{t.classList.toggle("burger--active"),r.classList.toggle("active"),c.classList.toggle("lock")})}if(document.querySelector(".header")){var e=".header";const o=document.querySelector(e);window.addEventListener("scroll",function(){0<window.scrollY?o.classList.add("header--scroll"):o.classList.remove("header--scroll")})}});